mapper.select:
SELECT UE.*
  FROM USER UE
 WHERE UE.USER_ID = #{userId, jdbcType=VARCHAR}

SELECT COUNT(1)
  FROM USER UE
 WHERE UE.USER_ID = #{userId, jdbcType=VARCHAR}

mapper.update:
UPDATE USER UE
   SET UE.PASSWORD_UPDATE_DATE = NOW()
     , UE.USER_NAME = #{userName, jdbcType=VARCHAR}

         , UE.PASSWORD = #{password, jdbcType=VARCHAR}

 WHERE UE.USER_ID = #{userId, jdbcType=VARCHAR}
