mapper.select:
SELECT COUNT(*)
  FROM QUIZ
 WHERE CATEGORY = #{category}

mapper.insert:
INSERT
  INTO QUIZ
     ( QUIZ_ID
     , CATEGORY
     , QUIZ_INFO
     , INSERT-ID
     , INSERT_DATE
     , MODIFY_ID
     , MODIFY_DATE
     , NO )
VALUES
     ( #{quizID, jdbcType=NUMERIC}
     , #{category, jdbcType=VARCHAR}
     , #{quizInfo, jdbcType=VARCHAR}
     , #{insertId, jdbcType=VARCHAR}
     , #{insertDate, jdbcType=DATE}
     , #{modifyId, jdbcType=VARCHAR}
     , #{modifyDate, jdbcType=DATE}
     , #{no, jdbcType=NUMERIC} )
